mapper.select:
SELECT COUNT(NO)
  FROM BOARD
 WHERE STATE = 0
   AND ${column} LIKE #{query}
       TITLE LIKE #{query} OR AUTHOR LIKE #{query}

SELECT B.*
  FROM (SELECT ROWNUM AS RN, A.*
  		  FROM (SELECT *
  		  		  FROM BOARD
  		  		 WHERE STATE = 0
  		  		   AND ${column} LIKE #{query}
  		  		       TITLE LIKE #{query} OR AUTHOR LIKE #{query}
  		  		 ORDER BY GROUPNO DESC, DEPTH ASC, GROUPORD DESC) A ) B
 WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}

mapper.update:
UPDATE BOARD
   SET STATE = -1
 WHERE NO = ${no}
